// workspaces contains functionality for using the workspace concept of the CLI to connect and interact
// with the remote endpoints that are described by the workspace concept
// (Radius control plane, environment, et al).
package workspaces

import (
	"testing"

	"github.com/stretchr/testify/require"
)

//
// <AiDoc: The following comments were generated by OpenAI explaining the code as it existed at the time of this commit>
//	The previous function is testing if the given Kubernetes context is the same as the one stored in the Workspace object. 
//	It returns true if the contexts are the same, and false if they are not.
//
// </AiDoc: End of OpenAI comments>
func Test_IsSameKubernetesContext(t *testing.T) {
	ctx := "kind-kind"
	ws := Workspace{
		Name: "my_workspace",
		Connection: map[string]any{
			"kind":    "kubernetes",
			"context": "kind-kind",
		},
	}

	isSame := ws.IsSameKubernetesContext(ctx)
	require.Equal(t, isSame, true)

	ctx = "aks1"
	isSame = ws.IsSameKubernetesContext(ctx)
	require.Equal(t, isSame, false)

}
